    private AppCompatAutoCompleteTextView autoTextView;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_add_medication1);
        autoTextView = (AppCompatAutoCompleteTextView) findViewById(R.id.autoTextView);
        ArrayAdapter<String> adapter = new ArrayAdapter<String>(this, android.R.layout.select_dialog_item, produto);
        autoTextView.setThreshold(1); //will start working from first character
        autoTextView.setAdapter(adapter);

        greenbtn = (Button) findViewById(R.id.green_btn);

        greenbtn.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                buttonNewMedication2();
            }
        });
    }

    //Fechar teclado
    public void click(View v) {
        String newText = autoTextView.getText().toString();
        //textViewResult.setText(newText);
        closeKeyboard();
    }

    private void closeKeyboard() {
        View view = this.getCurrentFocus();
        if (view != null) {
            InputMethodManager imm = (InputMethodManager) getSystemService(Context.INPUT_METHOD_SERVICE);
            imm.hideSoftInputFromWindow(view.getWindowToken(), 0);
        }
    }

    //Botão continuar(chamar nova tela)
    private void buttonNewMedication2() {
        String novoMedicamento = autoTextView.getText().toString().trim().replaceAll("\\s+", " ");

        for (int i = 0; i < produto.length; i++) {
            if (produto[i].equals(novoMedicamento)) {
                Intent intent = new Intent(AddMedication1.this, AddMedication2.class);
                intent.putExtra("key", autoTextView.getText().toString().trim().replaceAll("\\s+", " "));
                startActivity(intent);
                return;
            }
        }
        Toast.makeText(this, "NOME INCORRETO", Toast.LENGTH_LONG).show();
    }

    //voltar TEXTE
    public void btnVoltar(View v) {
        startActivity(new Intent(this, MainActivity.class));
    }


